import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Card, CardHeader, CardBody, CardFooter } from '../components/ui/Card';
import { Button } from '../components/ui/Button';
import { Input } from '../components/ui/Input';
import { Textarea } from '../components/ui/Textarea';
import { useToast } from '../contexts/ToastContext';
import { interviewService } from '../services/interviewService';
import { avatarService } from '../services/avatarService';
import { aiService } from '../services/aiService';
import { 
  User, 
  Mail, 
  Phone, 
  Play, 
  Pause, 
  Mic, 
  MicOff, 
  Send,
  CheckCircle,
  Clock,
  AlertCircle
} from 'lucide-react';

const PublicInterview = () => {
  const { shareableToken } = useParams();
  const navigate = useNavigate();
  const { toast } = useToast();

  const [interview, setInterview] = useState(null);
  const [candidate, setCandidate] = useState({
    full_name: '',
    email: '',
    phone: ''
  });
  const [session, setSession] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [avatarElement, setAvatarElement] = useState(null);

  useEffect(() => {
    loadInterview();
  }, [shareableToken]);

  useEffect(() => {
    if (session && interview) {
      initializeAvatar();
    }
  }, [session, interview]);

  const loadInterview = async () => {
    try {
      const data = await interviewService.getPublicInterview(shareableToken);
      setInterview(data);
    } catch (error) {
      toast.error('Failed to load interview: ' + (error.response?.data?.detail || error.message));
      navigate('/');
    } finally {
      setLoading(false);
    }
  };

  const initializeAvatar = () => {
    if (avatarElement) {
      avatarElement.remove();
    }

    const container = document.getElementById('avatar-container');
    if (container && interview.avatar) {
      const avatar = avatarService.createAvatarVisuals(interview.avatar, container);
      setAvatarElement(avatar);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const candidateData = await interviewService.registerCandidate(shareableToken, candidate);
      setCandidate({ ...candidate, id: candidateData.id });
      toast.success('Registration successful! Starting interview...');
    } catch (error) {
      toast.error('Registration failed: ' + (error.response?.data?.detail || error.message));
    }
  };

  const startInterview = async () => {
    try {
      const sessionData = await interviewService.startInterviewSession(shareableToken, candidate.id);
      setSession(sessionData);
      toast.success('Interview started!');
    } catch (error) {
      toast.error('Failed to start interview: ' + (error.response?.data?.detail || error.message));
    }
  };

  const speakQuestion = async (question) => {
    if (!interview.voice) return;

    try {
      setIsPlaying(true);
      const audioBlob = await aiService.generateTTS(question.text, interview.voice);
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);

      audio.onended = () => {
        setIsPlaying(false);
        URL.revokeObjectURL(audioUrl);
      };

      await audio.play();

      // Animate avatar while speaking
      if (avatarElement) {
        avatarService.startSpeaking(avatarElement);
        setTimeout(() => avatarService.stopSpeaking(avatarElement), audio.duration * 1000);
      }
    } catch (error) {
      toast.error('Failed to play question audio: ' + error.message);
      setIsPlaying(false);
    }
  };

  const startRecording = () => {
    setIsRecording(true);
    // Implement audio recording logic here
    // This would use Web Audio API or MediaRecorder
  };

  const stopRecording = () => {
    setIsRecording(false);
    // Process recorded audio and transcribe
  };

  const submitResponse = async () => {
    if (!response.trim()) {
      toast.error('Please provide a response');
      return;
    }

    try {
      setSubmitting(true);
      await interviewService.submitResponse(session.session_id, currentQuestionIndex, response);

      if (currentQuestionIndex < interview.questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setResponse('');
        toast.success('Response submitted! Next question...');
      } else {
        await completeInterview();
      }
    } catch (error) {
      toast.error('Failed to submit response: ' + (error.response?.data?.detail || error.message));
    } finally {
      setSubmitting(false);
    }
  };

  const completeInterview = async () => {
    try {
      await interviewService.completeSession(session.session_id);
      toast.success('Interview completed successfully!');
      navigate('/interview-complete');
    } catch (error) {
      toast.error('Failed to complete interview: ' + (error.response?.data?.detail || error.message));
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading interview...</p>
        </div>
      </div>
    );
  }

  if (!interview) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertCircle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Interview Not Found</h1>
          <p className="text-gray-600 mb-4">The interview you're looking for doesn't exist or is no longer active.</p>
          <Button onClick={() => navigate('/')}>Go Home</Button>
        </div>
      </div>
    );
  }

  // Registration form
  if (!candidate.id) {
    return (
      <div className="min-h-screen bg-gray-50 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <Card>
            <CardHeader>
              <h1 className="text-2xl font-bold text-gray-900">Interview Registration</h1>
              <p className="text-gray-600">Please provide your details to start the interview</p>
            </CardHeader>
            <CardBody>
              <div className="space-y-4">
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-2">Interview Details</h3>
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <p><strong>Position:</strong> {interview.job_role}</p>
                    <p><strong>Type:</strong> {interview.interview_type}</p>
                    <p><strong>Difficulty:</strong> {interview.difficulty}</p>
                    <p><strong>Questions:</strong> {interview.questions.length}</p>
                  </div>
                </div>

                <form onSubmit={handleRegister} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Full Name *
                    </label>
                    <Input
                      type="text"
                      value={candidate.full_name}
                      onChange={(e) => setCandidate({...candidate, full_name: e.target.value})}
                      required
                      placeholder="Enter your full name"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Email *
                    </label>
                    <Input
                      type="email"
                      value={candidate.email}
                      onChange={(e) => setCandidate({...candidate, email: e.target.value})}
                      required
                      placeholder="Enter your email"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Phone
                    </label>
                    <Input
                      type="tel"
                      value={candidate.phone}
                      onChange={(e) => setCandidate({...candidate, phone: e.target.value})}
                      placeholder="Enter your phone number"
                    />
                  </div>

                  <Button type="submit" className="w-full">
                    Register & Start Interview
                  </Button>
                </form>
              </div>
            </CardBody>
          </Card>
        </div>
      </div>
    );
  }

  // Interview session
  if (!session) {
    return (
      <div className="min-h-screen bg-gray-50 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <Card>
            <CardHeader>
              <h1 className="text-2xl font-bold text-gray-900">Ready to Start?</h1>
              <p className="text-gray-600">Click below to begin your interview</p>
            </CardHeader>
            <CardBody>
              <div className="text-center">
                <div className="bg-blue-50 p-6 rounded-lg mb-6">
                  <h3 className="text-lg font-medium text-gray-900 mb-2">Interview Details</h3>
                  <p><strong>Position:</strong> {interview.job_role}</p>
                  <p><strong>Questions:</strong> {interview.questions.length}</p>
                  <p><strong>Estimated Time:</strong> {interview.questions.length * 3} minutes</p>
                </div>

                <Button onClick={startInterview} size="lg">
                  <Play className="h-5 w-5 mr-2" />
                  Start Interview
                </Button>
              </div>
            </CardBody>
          </Card>
        </div>
      </div>
    );
  }

  // Active interview
  const currentQuestion = interview.questions[currentQuestionIndex];
  const progress = ((currentQuestionIndex + 1) / interview.questions.length) * 100;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-6xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-xl font-bold text-gray-900">{interview.job_role} Interview</h1>
              <p className="text-sm text-gray-600">Question {currentQuestionIndex + 1} of {interview.questions.length}</p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center text-sm text-gray-600">
                <Clock className="h-4 w-4 mr-1" />
                {Math.ceil((interview.questions.length - currentQuestionIndex) * 3)} min left
              </div>
              <div className="w-32 bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Avatar Section */}
          <div className="space-y-4">
            <Card>
              <CardHeader>
                <h3 className="text-lg font-medium text-gray-900">AI Interviewer</h3>
              </CardHeader>
              <CardBody>
                <div 
                  id="avatar-container" 
                  className="w-full h-64 bg-gray-100 rounded-lg flex items-center justify-center"
                >
                  {!avatarElement && (
                    <div className="text-center text-gray-500">
                      <User className="h-16 w-16 mx-auto mb-2" />
                      <p>Loading AI Avatar...</p>
                    </div>
                  )}
                </div>
              </CardBody>
            </Card>

            {/* Question Controls */}
            <Card>
              <CardHeader>
                <h3 className="text-lg font-medium text-gray-900">Question Audio</h3>
              </CardHeader>
              <CardBody>
                <div className="flex items-center space-x-3">
                  <Button
                    onClick={() => speakQuestion(currentQuestion)}
                    disabled={isPlaying}
                    variant={isPlaying ? "secondary" : "primary"}
                  >
                    {isPlaying ? <Pause className="h-4 w-4 mr-2" /> : <Play className="h-4 w-4 mr-2" />}
                    {isPlaying ? 'Playing...' : 'Play Question'}
                  </Button>
                </div>
              </CardBody>
            </Card>
          </div>

          {/* Question and Response Section */}
          <div className="space-y-4">
            <Card>
              <CardHeader>
                <h3 className="text-lg font-medium text-gray-900">Current Question</h3>
              </CardHeader>
              <CardBody>
                <div className="space-y-4">
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <p className="text-gray-900 font-medium">{currentQuestion.text}</p>
                    {currentQuestion.tags && currentQuestion.tags.length > 0 && (
                      <div className="mt-2 flex flex-wrap gap-2">
                        {currentQuestion.tags.map((tag, index) => (
                          <span 
                            key={index}
                            className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full"
                          >
                            {tag}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </CardBody>
            </Card>

            <Card>
              <CardHeader>
                <h3 className="text-lg font-medium text-gray-900">Your Response</h3>
              </CardHeader>
              <CardBody>
                <div className="space-y-4">
                  <Textarea
                    value={response}
                    onChange={(e) => setResponse(e.target.value)}
                    placeholder="Type your response here..."
                    rows={6}
                    className="w-full"
                  />

                  <div className="flex items-center space-x-3">
                    <Button
                      onClick={startRecording}
                      disabled={isRecording}
                      variant={isRecording ? "secondary" : "outline"}
                    >
                      {isRecording ? <MicOff className="h-4 w-4 mr-2" /> : <Mic className="h-4 w-4 mr-2" />}
                      {isRecording ? 'Stop Recording' : 'Record Audio'}
                    </Button>
                  </div>
                </div>
              </CardBody>
              <CardFooter>
                <Button
                  onClick={submitResponse}
                  disabled={!response.trim() || submitting}
                  className="w-full"
                >
                  {submitting ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Submitting...
                    </>
                  ) : (
                    <>
                      <Send className="h-4 w-4 mr-2" />
                      {currentQuestionIndex < interview.questions.length - 1 ? 'Submit & Next' : 'Complete Interview'}
                    </>
                  )}
                </Button>
              </CardFooter>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PublicInterview;
