// Replace the entire contents of this file

import api from './api';
import publicApi from './publicApi'; // Use the public api instance for non-auth calls

export const aiService = {
  /**
   * Generate speech from text using the PUBLIC endpoint.
   * @param {string} text - Text to convert to speech
   * @returns {Promise<Blob>} Audio blob
   */
  async generateTTS(text) {
    if (!text || text.trim() === '') {
      const errorMessage = 'TTS Error: Text cannot be empty.';
      console.error(errorMessage);
      return Promise.reject(new Error(errorMessage));
    }

    try {
      // Use publicApi to call the unauthenticated /api/public/tts endpoint
      // Send a JSON payload instead of FormData
      const response = await publicApi.post('/public/tts', { text }, {
        responseType: 'blob', // We expect an audio file back
      });

      return response.data;
    } catch (error) {
      console.error('TTS generation failed:', error);
      throw error;
    }
  },

  /**
   * Transcribe audio file using the SECURE endpoint.
   * @param {File} audioFile - Audio file to transcribe
   * @returns {Promise<Object>} Transcription result
   */
  async transcribeAudio(audioFile) {
    try {
      const formData = new FormData();
      formData.append('audio_file', audioFile);

      // Use the secure 'api' instance for calls that require a user to be logged in
      const response = await api.post('/ai/transcribe', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      return response.data;
    } catch (error) {
      console.error('Transcription failed:', error);
      throw error;
    }
  },

  /**
   * Get available voices using the SECURE endpoint.
   * @returns {Promise<Object>} Available voices
   */
  async getAvailableVoices() {
    try {
      const response = await api.get('/ai/voices');
      return response.data;
    } catch (error) {
      console.error('Failed to get voices:', error);
      throw error;
    }
  },
};
