import React, { createContext, useContext, useState, useEffect } from 'react';
import { authService } from '../services/authService';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is already logged in
    const accessToken = localStorage.getItem('access_token');
    if (accessToken) {
      console.log('Access token found, fetching user data...');
      authService.getCurrentUser()
        .then(result => {
          console.log('User data fetch result:', result);
          if (result && result.success === true && result.data) {
            setUser(result.data);
          } else {
            console.log('Failed to fetch user data, clearing tokens');
            localStorage.removeItem('access_token');
            localStorage.removeItem('refresh_token');
            setUser(null);
          }
        })
        .catch((error) => {
          console.error('Failed to fetch user data:', error);
          // Clear tokens on any error during user data fetch
          localStorage.removeItem('access_token');
          localStorage.removeItem('refresh_token');
          setUser(null);
        })
        .finally(() => {
          setLoading(false);
        });
    } else {
      console.log('No access token found');
      setLoading(false);
    }
  }, []);

  const login = async (email, password) => {
    try {
      const result = await authService.login(email, password);

      // Handle safe API call response format
      if (result && result.success === true && result.data) {
        const responseData = result.data;
        const access_token = responseData?.data?.access_token || responseData?.access_token;
        const refresh_token = responseData?.data?.refresh_token || responseData?.refresh_token;
        const userData = responseData?.data?.user || responseData?.user;

        if (access_token && refresh_token) {
          localStorage.setItem('access_token', access_token);
          localStorage.setItem('refresh_token', refresh_token);
          setUser(userData);
          return { success: true, data: userData };
        } else {
          return { 
            success: false, 
            error: 'Invalid response format from login endpoint'
          };
        }
      } else {
        return { 
          success: false, 
          error: result?.error || 'Login failed. Please check your credentials.'
        };
      }
    } catch (error) {
      console.error('Login error:', error);
      return { 
        success: false, 
        error: 'An unexpected error occurred. Please try again.'
      };
    }
  };

  const register = async (userData) => {
    try {
      const result = await authService.register(userData);

      // Handle safe API call response format
      if (result && result.success === true && result.data) {
        return { success: true, data: result.data };
      } else {
        return { 
          success: false, 
          error: result?.error || 'Registration failed. Please try again.'
        };
      }
    } catch (error) {
      console.error('Registration error:', error);
      return { 
        success: false, 
        error: 'An unexpected error occurred. Please try again.'
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    setUser(null);
  };

  const updateUser = (userData) => {
    setUser(userData);
  };

  const value = {
    user,
    loading,
    login,
    register,
    logout,
    updateUser
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}
