import React, { useState } from 'react';
import { X, Sparkles, Loader } from 'lucide-react';
import Button from './ui/Button';
import Input from './ui/Input';
import Textarea from './ui/Textarea';
import Select from './ui/Select';
import Checkbox from './ui/Checkbox';

const AIQuestionModal = ({ isOpen, onClose, onQuestionsGenerated, interviewService }) => {
  const [formData, setFormData] = useState({
    job_role: '',
    job_description: '',
    difficulty: 'medium',
    focus: [],
    number_of_questions: 5
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const focusAreas = interviewService.getFocusAreas();
  const difficultyLevels = interviewService.getDifficultyLevels();

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleFocusChange = (focusValue, checked) => {
    if (checked) {
      setFormData(prev => ({
        ...prev,
        focus: [...prev.focus, focusValue]
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        focus: prev.focus.filter(f => f !== focusValue)
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.job_role.trim()) {
      newErrors.job_role = 'Job role is required';
    }
    
    if (!formData.job_description.trim()) {
      newErrors.job_description = 'Job description is required';
    }
    
    if (formData.focus.length === 0) {
      newErrors.focus = 'Please select at least one focus area';
    }
    
    if (formData.number_of_questions < 1 || formData.number_of_questions > 20) {
      newErrors.number_of_questions = 'Number of questions must be between 1 and 20';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleGenerateQuestions = async () => {
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    try {
      const response = await interviewService.generateQuestions(formData);
      
      if (response.success && response.questions) {
        onQuestionsGenerated(response.questions);
        onClose();
        // Reset form
        setFormData({
          job_role: '',
          job_description: '',
          difficulty: 'medium',
          focus: [],
          number_of_questions: 5
        });
      }
    } catch (error) {
      console.error('Failed to generate questions:', error);
      setErrors({ general: 'Failed to generate questions. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-2">
            <Sparkles className="h-6 w-6 text-blue-600" />
            <h2 className="text-xl font-semibold text-gray-900">AI Question Generation</h2>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <div className="p-6 space-y-6">
          {errors.general && (
            <div className="bg-red-50 border border-red-200 rounded-md p-4">
              <p className="text-sm text-red-600">{errors.general}</p>
            </div>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Job Role *
              </label>
              <Input
                value={formData.job_role}
                onChange={(e) => handleInputChange('job_role', e.target.value)}
                placeholder="e.g., Frontend Developer, Data Scientist"
                error={!!errors.job_role}
              />
              {errors.job_role && (
                <p className="mt-1 text-sm text-red-600">{errors.job_role}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Difficulty Level
              </label>
              <Select
                value={formData.difficulty}
                onChange={(e) => handleInputChange('difficulty', e.target.value)}
              >
                {difficultyLevels.map((level) => (
                  <option key={level.value} value={level.value}>
                    {level.label}
                  </option>
                ))}
              </Select>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Job Description *
            </label>
            <Textarea
              value={formData.job_description}
              onChange={(e) => handleInputChange('job_description', e.target.value)}
              placeholder="Describe the role, responsibilities, and requirements..."
              rows={4}
              error={!!errors.job_description}
            />
            {errors.job_description && (
              <p className="mt-1 text-sm text-red-600">{errors.job_description}</p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Focus Areas *
            </label>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
              {focusAreas.map((area) => (
                <Checkbox
                  key={area.value}
                  checked={formData.focus.includes(area.value)}
                  onChange={(e) => handleFocusChange(area.value, e.target.checked)}
                >
                  <span className="text-sm">{area.label}</span>
                </Checkbox>
              ))}
            </div>
            {errors.focus && (
              <p className="mt-1 text-sm text-red-600">{errors.focus}</p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Number of Questions
            </label>
            <Input
              type="number"
              value={formData.number_of_questions}
              onChange={(e) => handleInputChange('number_of_questions', parseInt(e.target.value))}
              min="1"
              max="20"
              error={!!errors.number_of_questions}
            />
            {errors.number_of_questions && (
              <p className="mt-1 text-sm text-red-600">{errors.number_of_questions}</p>
            )}
            <p className="mt-1 text-sm text-gray-500">
              Choose between 1 and 20 questions
            </p>
          </div>
        </div>

        <div className="flex items-center justify-end space-x-3 p-6 border-t border-gray-200">
          <Button
            variant="outline"
            onClick={onClose}
            disabled={loading}
          >
            Cancel
          </Button>
          <Button
            onClick={handleGenerateQuestions}
            loading={loading}
            disabled={loading}
          >
            <Sparkles className="h-4 w-4 mr-2" />
            Generate Questions
          </Button>
        </div>
      </div>
    </div>
  );
};

export default AIQuestionModal;
