import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Card, CardHeader, CardBody } from '../components/ui/Card';
import Button from '../components/ui/Button';
import Input from '../components/ui/Input';
import toast from 'react-hot-toast';
import { interviewService } from '../services/interviewService';
import { publicInterviewService } from '../services/publicInterviewService';
import { avatarService } from '../services/avatarService';
import { safeApiCallWithValidation, safeGet } from '../utils/errorHandler';
import { safeApiCall } from "../utils/errorHandler";
import { aiService } from '../services/aiService';
import { 
  Mic, 
  MicOff, 
  CheckCircle,
  Clock,
  AlertCircle,
  Upload,
  FileText,
  Camera
} from 'lucide-react';

const PublicInterview = () => {
  const { interviewId } = useParams();
  const navigate = useNavigate();

  const [step, setStep] = useState('loading'); // loading, register, permissions, ready, interview, complete
  const [interview, setInterview] = useState(null);
  const [candidate, setCandidate] = useState({
    name: '',
    email: '',
    phone: '',
    resume: null
  });
  const [sessionId, setSessionId] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isRecording, setIsRecording] = useState(false);
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [avatarElement, setAvatarElement] = useState(null);
  const [mediaStream, setMediaStream] = useState(null);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [interviewStarted, setInterviewStarted] = useState(false);

  // Enhanced permission states
  const [permissions, setPermissions] = useState({
    microphone: false,
    camera: false,
    screen: false
  });
  const [permissionError, setPermissionError] = useState(null);
  const [requestingPermissions, setRequestingPermissions] = useState(false);

  const videoRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const silenceTimeoutRef = useRef(null);
  const speechTimeoutRef = useRef(null);

  const loadInterview = useCallback(async () => {
    setLoading(true);

    const result = await safeApiCallWithValidation(
      () => publicInterviewService.getPublicInterview(interviewId),
      ['id', 'job_role', 'questions'],
      'Failed to load interview. Please check the link and try again.'
    );

    // Safely check result object and properties
    if (result && result.success === true && result.data) {
      setInterview(result.data);
      setStep('register');
    } else {
      const errorMessage = result?.error || 'Failed to load interview. Please check the link and try again.';
      toast.error(errorMessage);
      setStep('error');
    }

    setLoading(false);
  }, [interviewId, toast]);

  const initializeAvatar = useCallback(() => {
    if (avatarElement) return;

    try {
      const avatarContainer = document.getElementById('avatar-container');
      if (avatarContainer) {
        // Clear any existing content
        avatarContainer.innerHTML = '';

        const avatar = avatarService.createAvatarVisuals();
        if (avatar) {
          avatarContainer.appendChild(avatar);
          setAvatarElement(avatar);
          console.log('Avatar initialized successfully');
        } else {
          console.error('Failed to create avatar visuals');
          toast.error('Failed to initialize avatar. Please refresh the page.');
        }
      } else {
        console.error('Avatar container not found');
        toast.error('Avatar container not found. Please refresh the page.');
      }
    } catch (error) {
      console.error('Avatar initialization error:', error);
      toast.error('Failed to initialize avatar. Please refresh the page.');
    }
  }, [avatarElement, toast]);

  useEffect(() => {
    loadInterview();
  }, [loadInterview]);

  useEffect(() => {
    if (interview && step === 'interview') {
      initializeAvatar();
    }
  }, [interview, step, initializeAvatar]);

  // Cleanup media streams on unmount
// REPLACE your existing cleanup useEffect with this one

  useEffect(() => {
    // This return function is the cleanup that runs ONLY when the component unmounts
    return () => {
      console.log("Cleaning up interview component...");
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
      if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
        mediaRecorderRef.current.stop();
      }
      // Check if the audio context exists and is not already closed before closing it
      if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
        audioContextRef.current.close();
      }
      // Clear any running timers
      if (silenceTimeoutRef.current) {
        clearTimeout(silenceTimeoutRef.current);
      }
      if (speechTimeoutRef.current) {
        clearTimeout(speechTimeoutRef.current);
      }
    };
  }, []); // <-- IMPORTANT: Empty array means this runs only on unmount


  // REPLACE the existing handleCandidateRegistration function with this one
  const handleCandidateRegistration = async (e) => {
      e.preventDefault();
      setSubmitting(true);

      try {
          const formData = new FormData();
          formData.append('name', candidate.name);
          formData.append('email', candidate.email);
          formData.append('phone', candidate.phone);
          if (candidate.resume) {
              formData.append('resume_file', candidate.resume);
          }

          // The API call is now wrapped in its own try/catch to safely handle network errors
          const result = await publicInterviewService.registerCandidate(interviewId, formData);

          console.log('API Response:', result); // Log the raw response for debugging

          // Check for a successful response structure
          if (result && result.success && result.data?.candidate?.id && result.data?.session_id) {
              setCandidate(prev => ({ ...prev, id: result.data.candidate.id }));
              setSessionId(result.data.session_id);
              toast.success('Registration successful! Please grant permissions to start the interview.');
              setStep('permissions');
          } else {
              // If the structure is wrong or success is false, extract the error message safely
              const errorMessage = result?.error || result?.message || 'Registration failed due to an unknown error.';
              toast.error(errorMessage);
              console.error('Registration failed with response:', result);
          }

      } catch (err) {
          console.error("Caught Registration Error:", err);

          // Safely extract the error message from the caught error object
          // This is crucial for handling 4xx/5xx server errors
          const errorMessage = err?.response?.data?.error || err?.message || "An unexpected error occurred. Please check the console.";
          toast.error(errorMessage);

      } finally {
          setSubmitting(false);
      }
  };


  // Handle permission request step
  const handlePermissionRequest = async () => {
    setRequestingPermissions(true);
    const success = await requestAllPermissions();

    if (success) {
      setStep('ready');
      toast.success('All permissions granted! You can now start the interview.');
    } else {
      toast.error('Permissions are required to continue. Please try again.');
    }
    setRequestingPermissions(false);
  };

// REPLACE your old startInterview function with this one

// REPLACE your startInterview function with this final version

// REPLACE your existing startInterview function with this one

  const startInterview = async () => {
    setSubmitting(true);
    try {
      const result = await publicInterviewService.startSession(sessionId);

      if (result && result.success === true) {
        // Set the step to 'interview' to show the main screen
        setStep('interview');
        toast.success('Interview started!');

        // Ask the first question immediately.
        // The timeout ensures the component has re-rendered before we try to speak.
        setTimeout(() => {
          if (interview && interview.questions && interview.questions.length > 0) {
            askQuestion(interview.questions[0].text);
          }
        }, 100); // A short 100ms delay is enough

      } else {
        const errorMessage = result?.error || 'An unknown error occurred.';
        toast.error(errorMessage);
      }
    } catch (err) {
      console.error("Start Interview Error:", err);
      const errorMessage = err?.response?.data?.error || err?.message || 'Failed to start interview.';
      toast.error(errorMessage);
    } finally {
      setSubmitting(false);
    }
  };

  // Enhanced permission request function
  const requestAllPermissions = async () => {
    setRequestingPermissions(true);
    setPermissionError(null);

    const newPermissions = {
      microphone: false,
      camera: false,
      screen: false
    };

    try {
      // Request microphone and camera permissions
      console.log('Requesting microphone and camera permissions...');
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });

      newPermissions.microphone = true;
      newPermissions.camera = true;
      setMediaStream(mediaStream);

      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }

      console.log('Microphone and camera permissions granted');

      // Request screen recording permission
      try {
        console.log('Requesting screen recording permission...');
        const screenStream = await navigator.mediaDevices.getDisplayMedia({
          video: true,
          audio: true
        });

        newPermissions.screen = true;
        console.log('Screen recording permission granted');

        // Stop the screen stream as we just needed permission
        screenStream.getTracks().forEach(track => track.stop());
      } catch (screenError) {
        console.warn('Screen recording permission denied:', screenError);
        // Don't fail the entire flow if screen recording is denied
        toast.warning('Screen recording permission denied. Interview will continue without screen sharing.');
      }

      setPermissions(newPermissions);

      return true;
    } catch (error) {
      console.error('Media permission denied:', error);
      setPermissionError(error.message);
      return false;
    } finally {
      setRequestingPermissions(false);
    }
  };



  const askQuestion = async (questionText) => {
    if (!avatarElement) {
      console.warn('Avatar not initialized, cannot ask question');
      return;
    }

    if (!questionText || questionText.trim() === '') {
      console.warn('Empty question text provided');
      return;
    }

    setIsSpeaking(true);

    try {
      // Generate TTS audio for the question
      const audioBlob = await aiService.generateTTS(questionText);
      if (!audioBlob) {
        console.error('Failed to generate TTS audio');
        toast.error('Failed to generate speech. Please try again.');
        setIsSpeaking(false);
        return;
      }

      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);

      // Add error handling for audio playback
      audio.onerror = (e) => {
        console.error('Audio playback error:', e);
        toast.error('Failed to play audio. Please try again.');
        URL.revokeObjectURL(audioUrl);
        setIsSpeaking(false);
      };

      // Start lip sync animation
      try {
        avatarService.startLipSyncAnimation(avatarElement, audio);
      } catch (lipSyncError) {
        console.warn('Lip sync animation failed:', lipSyncError);
        // Continue without lip sync
      }

      audio.onended = () => {
        URL.revokeObjectURL(audioUrl);
        setIsSpeaking(false);
        // Start listening for response after avatar finishes speaking
        setTimeout(() => {
          startListening();
        }, 1000);
      };

      await audio.play();
    } catch (error) {
      console.error('Failed to ask question:', error);
      toast.error('Failed to ask question. Please try again.');
      setIsSpeaking(false);
    }
  };

  const startListening = () => {
    if (!mediaStream || isListening) return;

    setIsListening(true);
    startRecording();

    // Setup voice activity detection
    setupVoiceActivityDetection();
  };

  const setupVoiceActivityDetection = () => {
    if (!mediaStream || !audioContextRef.current) return;

    try {
      const audioContext = audioContextRef.current;
      const analyser = analyserRef.current;

      if (!analyser) return;

      const dataArray = new Uint8Array(analyser.frequencyBinCount);
      let isSpeaking = false;
      let silenceStartTime = null;
      const SILENCE_THRESHOLD = 2000; // 2 seconds of silence
      const SPEECH_THRESHOLD = 30; // Minimum volume to consider as speech

      const checkVoiceActivity = () => {
        if (!isListening) return;

        analyser.getByteFrequencyData(dataArray);
        const average = dataArray.reduce((a, b) => a + b) / dataArray.length;

        if (average > SPEECH_THRESHOLD) {
          // Speech detected
          if (!isSpeaking) {
            isSpeaking = true;
            silenceStartTime = null;
            console.log('Speech started');
          }
        } else {
          // Silence detected
          if (isSpeaking) {
            isSpeaking = false;
            silenceStartTime = Date.now();
            console.log('Speech ended, starting silence timer');
          }

          // Check if we've been silent long enough
          if (silenceStartTime && Date.now() - silenceStartTime > SILENCE_THRESHOLD) {
            console.log('Silence threshold reached, stopping recording');
            stopListening();
            return;
          }
        }

        requestAnimationFrame(checkVoiceActivity);
      };

      checkVoiceActivity();
    } catch (error) {
      console.error('Voice activity detection setup failed:', error);
    }
  };

  const stopListening = () => {
    if (!isListening) return;

    setIsListening(false);
    stopRecording();

    // Clear timeouts
    if (silenceTimeoutRef.current) {
      clearTimeout(silenceTimeoutRef.current);
    }
    if (speechTimeoutRef.current) {
      clearTimeout(speechTimeoutRef.current);
    }
  };

  const startRecording = () => {
    if (!mediaStream) {
      console.error('No media stream available for recording');
      toast.error('No media stream available. Please grant permissions first.');
      return;
    }

    try {
      // Setup audio context for voice activity detection
      if (!audioContextRef.current) {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
        analyserRef.current = audioContextRef.current.createAnalyser();
        analyserRef.current.fftSize = 256;

        const source = audioContextRef.current.createMediaStreamSource(mediaStream);
        source.connect(analyserRef.current);
      }

      // Check if MediaRecorder is supported
      if (!window.MediaRecorder) {
        console.error('MediaRecorder not supported');
        toast.error('Recording not supported in this browser.');
        return;
      }

      mediaRecorderRef.current = new MediaRecorder(mediaStream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = () => {
        if (audioChunksRef.current.length > 0) {
          const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
          setResponse(audioBlob);
          console.log('Recording completed, audio blob created');

          // Auto-submit response if we're in listening mode
          if (isListening) {
            setTimeout(() => {
              submitResponse();
            }, 500);
          }
        } else {
          console.warn('No audio data recorded');
          toast.warning('No audio was recorded. Please try again.');
        }
      };

      mediaRecorderRef.current.onerror = (event) => {
        console.error('MediaRecorder error:', event.error);
        toast.error('Recording error occurred. Please try again.');
        setIsRecording(false);
        setIsListening(false);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      console.log('Recording started');
    } catch (error) {
      console.error('Failed to start recording:', error);
      toast.error('Failed to start recording. Please try again.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      try {
        mediaRecorderRef.current.stop();
        setIsRecording(false);
        console.log('Recording stopped');
      } catch (error) {
        console.error('Failed to stop recording:', error);
        toast.error('Failed to stop recording. Please try again.');
        setIsRecording(false);
      }
    } else {
      console.warn('No active recording to stop');
    }
  };

  const submitResponse = async () => {
    if (!response) return;

    setSubmitting(true);

    const formData = new FormData();
    formData.append('question_number', currentQuestionIndex + 1);

    // Send transcript as response_text (backend will map it to transcript)
    formData.append('response_text', typeof response === 'string' ? response : '');

    if (response instanceof Blob) {
      formData.append('audio_file', response, 'response.wav');
    }

    const result = await safeApiCall(
      () => publicInterviewService.submitResponse(sessionId, currentQuestionIndex + 1, formData),
      'Failed to submit response. Please try again.'
    );

    // Safely check result object and properties
    if (result && result.success === true) {
      // Move to next question
      const nextIndex = currentQuestionIndex + 1;
      if (nextIndex < interview.questions.length) {
        setCurrentQuestionIndex(nextIndex);
        setResponse('');

        // Ask next question
        setTimeout(() => {
          askQuestion(interview.questions[nextIndex].text);
        }, 1000);
      } else {
        // Interview completed
        await completeInterview();
      }
    } else {
      const errorMessage = result?.error || 'Failed to submit response. Please try again.';
      toast.error(errorMessage);
    }

    setSubmitting(false);
  };

  const completeInterview = async () => {
    const result = await safeApiCall(
      () => publicInterviewService.completeSession(sessionId),
      'Failed to complete interview. Please try again.'
    );

    // Safely check result object and properties
    if (result && result.success === true) {
      setStep('complete');
      toast.success('Interview completed successfully! Thank you for your time.');
    } else {
      const errorMessage = result?.error || 'Failed to complete interview. Please try again.';
      toast.error(errorMessage);
    }
  };

  const handleResumeChange = (e) => {
    const file = e.target.files[0];
    if (file && (file.type === 'application/pdf' || file.type === 'application/msword' || file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')) {
      setCandidate(prev => ({ ...prev, resume: file }));
    } else {
      toast.error('Please upload a PDF or Word document.');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading interview...</p>
        </div>
      </div>
    );
  }

  if (step === 'error') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-50 to-pink-100">
        <Card className="max-w-md mx-auto">
          <CardBody className="text-center">
            <AlertCircle className="h-16 w-16 text-red-500 mx-auto mb-4" />
            <h2 className="text-xl font-semibold text-gray-900 mb-2">Interview Not Found</h2>
            <p className="text-gray-600 mb-4">The interview link you're trying to access is invalid or has expired.</p>
            <Button onClick={() => navigate('/')} variant="primary">
              Go Home
            </Button>
          </CardBody>
        </Card>
      </div>
    );
  }

  if (step === 'register') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <Card className="shadow-xl">
            <CardHeader className="text-center">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Interview Registration</h1>
              <p className="text-gray-600">Please provide your details to start the interview</p>
            </CardHeader>
            <CardBody>
              <form onSubmit={handleCandidateRegistration} className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Full Name *
                  </label>
                  <Input
                    type="text"
                    value={candidate.name}
                    onChange={(e) => setCandidate(prev => ({ ...prev, name: e.target.value }))}
                    required
                    placeholder="Enter your full name"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Email Address *
                  </label>
                  <Input
                    type="email"
                    value={candidate.email}
                    onChange={(e) => setCandidate(prev => ({ ...prev, email: e.target.value }))}
                    required
                    placeholder="Enter your email"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Phone Number *
                  </label>
                  <Input
                    type="tel"
                    value={candidate.phone}
                    onChange={(e) => setCandidate(prev => ({ ...prev, phone: e.target.value }))}
                    required
                    placeholder="Enter your phone number"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Resume (Optional)
                  </label>
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md hover:border-gray-400 transition-colors">
                    <div className="space-y-1 text-center">
                      <Upload className="mx-auto h-12 w-12 text-gray-400" />
                      <div className="flex text-sm text-gray-600">
                        <label htmlFor="resume-upload" className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blue-500">
                          <span>Upload a file</span>
                          <input
                            id="resume-upload"
                            name="resume-upload"
                            type="file"
                            className="sr-only"
                            accept=".pdf,.doc,.docx"
                            onChange={handleResumeChange}
                          />
                        </label>
                        <p className="pl-1">or drag and drop</p>
                      </div>
                      <p className="text-xs text-gray-500">PDF, DOC, DOCX up to 10MB</p>
                    </div>
                  </div>
                  {candidate.resume && (
                    <div className="mt-2 flex items-center text-sm text-green-600">
                      <FileText className="h-4 w-4 mr-2" />
                      {candidate.resume.name}
                    </div>
                  )}
                </div>

                <Button
                  type="submit"
                  variant="primary"
                  size="lg"
                  className="w-full"
                  loading={submitting}
                >
                  Register for Interview
                </Button>
              </form>
            </CardBody>
          </Card>
        </div>
      </div>
    );
  }

  if (step === 'permissions') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <Card className="shadow-xl">
            <CardHeader className="text-center">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Permission Required</h1>
              <p className="text-gray-600">To conduct the interview, we need access to your microphone, camera, and screen.</p>
            </CardHeader>
            <CardBody className="text-center">
              <div className="space-y-6 mb-8">
                {/* Permission Status */}
                <div className="space-y-4">
                  <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <Mic className="h-6 w-6 text-blue-500" />
                      <span className="font-medium">Microphone</span>
                    </div>
                    <div className={`px-3 py-1 rounded-full text-sm ${
                      permissions.microphone 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-600'
                    }`}>
                      {permissions.microphone ? 'Granted' : 'Required'}
                    </div>
                  </div>

                  <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <Camera className="h-6 w-6 text-blue-500" />
                      <span className="font-medium">Camera</span>
                    </div>
                    <div className={`px-3 py-1 rounded-full text-sm ${
                      permissions.camera 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-600'
                    }`}>
                      {permissions.camera ? 'Granted' : 'Required'}
                    </div>
                  </div>

                  <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <div className="h-6 w-6 bg-blue-500 rounded flex items-center justify-center">
                        <span className="text-white text-xs font-bold">S</span>
                      </div>
                      <span className="font-medium">Screen Recording</span>
                    </div>
                    <div className={`px-3 py-1 rounded-full text-sm ${
                      permissions.screen 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-600'
                    }`}>
                      {permissions.screen ? 'Granted' : 'Optional'}
                    </div>
                  </div>
                </div>

                {/* Error Message */}
                {permissionError && (
                  <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
                    <p className="text-red-700 text-sm">
                      <strong>Permission Error:</strong> {permissionError}
                    </p>
                  </div>
                )}

                {/* Instructions */}
                <div className="text-left bg-blue-50 p-4 rounded-lg">
                  <h3 className="font-semibold text-blue-900 mb-2">Instructions:</h3>
                  <ul className="text-sm text-blue-800 space-y-1">
                    <li> Click "Grant Permissions" to allow access to your devices</li>
                    <li> Make sure to allow access when prompted by your browser</li>
                    <li> Screen recording is optional but recommended for a complete interview experience</li>
                    <li> You can deny screen recording and still proceed with the interview</li>
                  </ul>
                </div>
              </div>

              <div className="space-y-3">
                <Button
                  onClick={handlePermissionRequest}
                  variant="primary"
                  size="lg"
                  className="w-full"
                  loading={requestingPermissions}
                  disabled={requestingPermissions}
                >
                  {requestingPermissions ? 'Requesting Permissions...' : 'Grant Permissions'}
                </Button>

                <Button
                  onClick={() => setStep('register')}
                  variant="secondary"
                  size="lg"
                  className="w-full"
                >
                  Back to Registration
                </Button>
              </div>
            </CardBody>
          </Card>
        </div>
      </div>
    );
  }

  if (step === 'ready') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <Card className="shadow-xl">
            <CardHeader className="text-center">
              <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Ready to Start!</h1>
              <p className="text-gray-600">You're all set to begin the interview. Make sure you have a quiet environment and good internet connection.</p>
            </CardHeader>
            <CardBody className="text-center">
              <div className="space-y-4 mb-6">
                <div className="flex items-center justify-center space-x-2 text-gray-600">
                  <Camera className="h-5 w-5" />
                  <span>Camera will be used for video recording</span>
                </div>
                <div className="flex items-center justify-center space-x-2 text-gray-600">
                  <Mic className="h-5 w-5" />
                  <span>Microphone will be used for audio recording</span>
                </div>
                <div className="flex items-center justify-center space-x-2 text-gray-600">
                  <Clock className="h-5 w-5" />
                  <span>Interview duration: {interview?.duration_minutes || 30} minutes</span>
                </div>
              </div>

              <Button
                onClick={startInterview}
                variant="primary"
                size="lg"
                className="w-full"
                loading={submitting}
              >
                Start Interview
              </Button>
            </CardBody>
          </Card>
        </div>
      </div>
    );
  }

  if (step === 'interview') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-indigo-100">
        <div className="container mx-auto px-4 py-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* AI Avatar */}
            <div className="space-y-4">
              <Card>
                <CardHeader>
                  <h3 className="text-lg font-semibold">AI Interviewer</h3>
                </CardHeader>
                <CardBody>
                  <div id="avatar-container" className="h-64 bg-gray-100 rounded-lg flex items-center justify-center">
                    <div className="text-gray-500">AI Avatar Loading...</div>
                  </div>
                </CardBody>
              </Card>

              {/* Current Question */}
              {interview?.questions && interview.questions[currentQuestionIndex] && (
                <Card>
                  <CardHeader>
                    <h3 className="text-lg font-semibold">Current Question</h3>
                  </CardHeader>
                  <CardBody>
                    <p className="text-gray-700">
                      {interview.questions[currentQuestionIndex].text}
                    </p>
                    <div className="mt-4 flex flex-wrap gap-2">
                      {interview.questions[currentQuestionIndex].tags?.map((tag, index) => (
                        <span key={index} className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                          {tag}
                        </span>
                      ))}
                    </div>
                  </CardBody>
                </Card>
              )}
            </div>

            {/* Candidate Video and Controls */}
            <div className="space-y-4">
              <Card>
                <CardHeader>
                  <h3 className="text-lg font-semibold">Your Video</h3>
                </CardHeader>
                <CardBody>
                  <video
                    ref={videoRef}
                    autoPlay
                    muted
                    className="w-full h-48 bg-gray-900 rounded-lg"
                  />
                </CardBody>
              </Card>

              {/* Recording Controls */}
              <Card>
                <CardHeader>
                  <h3 className="text-lg font-semibold">
                    {isSpeaking ? 'AI is Speaking...' : 
                     isListening ? 'Listening for your response...' : 
                     isRecording ? 'Recording your response...' : 
                     'Interview Status'}
                  </h3>
                </CardHeader>
                <CardBody>
                  <div className="space-y-4">
                    {interviewStarted ? (
                      <div className="text-center space-y-4">
                        {isSpeaking && (
                          <div className="flex items-center justify-center space-x-2 text-blue-600">
                            <div className="animate-pulse w-3 h-3 bg-blue-600 rounded-full"></div>
                            <span>AI Avatar is asking the question...</span>
                          </div>
                        )}

                        {isListening && (
                          <div className="flex items-center justify-center space-x-2 text-green-600">
                            <div className="animate-pulse w-3 h-3 bg-green-600 rounded-full"></div>
                            <span>Listening for your response...</span>
                          </div>
                        )}

                        {isRecording && !isListening && (
                          <div className="flex items-center justify-center space-x-2 text-red-600">
                            <div className="animate-pulse w-3 h-3 bg-red-600 rounded-full"></div>
                            <span>Recording your response...</span>
                          </div>
                        )}

                        {submitting && (
                          <div className="flex items-center justify-center space-x-2 text-purple-600">
                            <div className="animate-spin w-4 h-4 border-2 border-purple-600 border-t-transparent rounded-full"></div>
                            <span>Processing your response...</span>
                          </div>
                        )}

                        {!isSpeaking && !isListening && !isRecording && !submitting && (
                          <div className="text-gray-600">
                            <p>Waiting for the next question...</p>
                          </div>
                        )}

                        {/* Manual controls for debugging/fallback */}
                        <div className="pt-4 border-t border-gray-200">
                          <p className="text-sm text-gray-500 mb-2">Manual Controls (if needed):</p>
                          <div className="flex justify-center space-x-2">
                            <Button
                              onClick={isRecording ? stopRecording : startRecording}
                              variant={isRecording ? "danger" : "primary"}
                              size="sm"
                              disabled={isSpeaking || submitting}
                            >
                              {isRecording ? (
                                <>
                                  <MicOff className="h-4 w-4 mr-1" />
                                  Stop
                                </>
                              ) : (
                                <>
                                  <Mic className="h-4 w-4 mr-1" />
                                  Record
                                </>
                              )}
                            </Button>

                            {response && (
                              <Button
                                onClick={submitResponse}
                                variant="success"
                                size="sm"
                                loading={submitting}
                                disabled={isSpeaking || isListening}
                              >
                                Submit
                              </Button>
                            )}
                          </div>
                        </div>
                      </div>
                    ) : (
                      <div className="text-center text-gray-600">
                        <p>Interview will start automatically...</p>
                      </div>
                    )}
                  </div>
                </CardBody>
              </Card>

              {/* Progress */}
              <Card>
                <CardHeader>
                  <h3 className="text-lg font-semibold">Progress</h3>
                </CardHeader>
                <CardBody>
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span>Question {currentQuestionIndex + 1} of {interview?.questions?.length || 0}</span>
                      <span>{Math.round(((currentQuestionIndex + 1) / (interview?.questions?.length || 1)) * 100)}%</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div
                        className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                        style={{ width: `${((currentQuestionIndex + 1) / (interview?.questions?.length || 1)) * 100}%` }}
                      />
                    </div>
                  </div>
                </CardBody>
              </Card>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (step === 'complete') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 py-12">
        <div className="max-w-2xl mx-auto px-4">
          <Card className="shadow-xl">
            <CardHeader className="text-center">
              <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Interview Completed!</h1>
              <p className="text-gray-600">Thank you for completing the interview. Your responses have been recorded and will be reviewed.</p>
            </CardHeader>
            <CardBody className="text-center">
              <div className="space-y-4">
                <p className="text-gray-700">
                  We'll be in touch soon with the results. Thank you for your time and interest!
                </p>
                <Button onClick={() => navigate('/')} variant="primary">
                  Return Home
                </Button>
              </div>
            </CardBody>
          </Card>
        </div>
      </div>
    );
  }

  return null;
};

export default PublicInterview;
