import api from './api';

export const publicInterviewService = {
  // Get public interview configuration
  async getPublicInterview(interviewId) {
    try {
      const response = await api.get(`/public/interview/${interviewId}`);
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Register candidate for interview
  async registerCandidate(interviewId, candidateData) {
    try {
      const response = await api.post(`/public/interview/${interviewId}/register`, candidateData);
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Start interview session
  async startSession(interviewId, candidateId) {
    try {
      const formData = new FormData();
      formData.append('candidate_id', candidateId);
      
      const response = await api.post(`/public/interview/${interviewId}/start`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Get session details
  async getSession(sessionId) {
    try {
      const response = await api.get(`/public/session/${sessionId}`);
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Submit response
  async submitResponse(sessionId, questionNumber, responseData) {
    try {
      const formData = new FormData();
      formData.append('question_number', questionNumber);
      
      if (responseData.response_text) {
        formData.append('response_text', responseData.response_text);
      }
      
      if (responseData.audio_file) {
        formData.append('audio_file', responseData.audio_file);
      }
      
      if (responseData.video_file) {
        formData.append('video_file', responseData.video_file);
      }

      const response = await api.post(`/public/session/${sessionId}/submit-response`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Complete session
  async completeSession(sessionId) {
    try {
      const response = await api.post(`/public/session/${sessionId}/complete`);
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Get session responses
  async getSessionResponses(sessionId) {
    try {
      const response = await api.get(`/public/session/${sessionId}/responses`);
      return response.data;
    } catch (error) {
      throw error;
    }
  }
};
