/**
 * Utility functions for consistent error handling across the application
 */

/**
 * Safely extracts error message from various error response formats
 * @param {Error} error - The error object from try/catch
 * @param {string} fallbackMessage - Default message if no error details found
 * @returns {string} - Safe error message to display to user
 */
export const getErrorMessage = (error, fallbackMessage = 'An unexpected error occurred') => {
  if (!error) return fallbackMessage;
  
  // Check for Axios response error
  if (error.response?.data) {
    const data = error.response.data;
    
    // FastAPI error format: { "detail": "..." }
    if (data.detail) return data.detail;
    
    // Custom error format: { "error": "..." }
    if (data.error) return data.error;
    
    // Multiple errors format: { "errors": [...] }
    if (data.errors && Array.isArray(data.errors)) {
      return data.errors.join(', ');
    }
    
    // Message format: { "message": "..." }
    if (data.message) return data.message;
  }
  
  // Check for network error
  if (error.message) return error.message;
  
  // Check for custom error properties
  if (error.error) return error.error;
  
  return fallbackMessage;
};

/**
 * Safely checks if a response object has the expected structure
 * @param {any} response - The response object to validate
 * @param {string[]} requiredFields - Array of required field paths (e.g., ['candidate.id', 'session_id'])
 * @returns {boolean} - True if response has all required fields
 */
export const validateResponse = (response, requiredFields = []) => {
  if (!response || typeof response !== 'object') return false;
  
  return requiredFields.every(field => {
    const fieldPath = field.split('.');
    let current = response;
    
    for (const key of fieldPath) {
      if (current && typeof current === 'object' && key in current) {
        current = current[key];
      } else {
        return false;
      }
    }
    
    return current !== null && current !== undefined;
  });
};

/**
 * Safely extracts data from response with validation
 * @param {any} response - The response object
 * @param {string[]} requiredFields - Array of required field paths
 * @param {string} errorMessage - Error message if validation fails
 * @returns {Object} - { success: boolean, data: any, error: string }
 */
export const safeResponseHandler = (response, requiredFields = [], errorMessage = 'Invalid response from server') => {
  if (validateResponse(response, requiredFields)) {
    return { success: true, data: response };
  } else {
    console.error('Response validation failed:', response);
    return { success: false, data: null, error: errorMessage };
  }
};

/**
 * Wrapper for API calls with consistent error handling
 * @param {Function} apiCall - The API function to call
 * @param {string} fallbackMessage - Default error message
 * @returns {Promise<Object>} - { success: boolean, data: any, error: string }
 */
export const safeApiCall = async (apiCall, fallbackMessage = 'API call failed') => {
  try {
    const response = await apiCall();
    return { success: true, data: response, error: null };
  } catch (error) {
    const errorMessage = getErrorMessage(error, fallbackMessage);
    console.error('API call failed:', error);
    return { success: false, data: null, error: errorMessage };
  }
};

/**
 * Wrapper for API calls with response validation
 * @param {Function} apiCall - The API function to call
 * @param {string[]} requiredFields - Array of required field paths
 * @param {string} fallbackMessage - Default error message
 * @returns {Promise<Object>} - { success: boolean, data: any, error: string }
 */
export const safeApiCallWithValidation = async (apiCall, requiredFields = [], fallbackMessage = 'API call failed') => {
  try {
    const response = await apiCall();
    return safeResponseHandler(response, requiredFields, fallbackMessage);
  } catch (error) {
