import React, { createContext, useContext, useState, useEffect } from 'react';
import { authService } from '../services/authService';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is already logged in
    const accessToken = localStorage.getItem('access_token');
    if (accessToken) {
      console.log('Access token found, fetching user data...');
      authService.getCurrentUser()
        .then(userData => {
          console.log('User data fetched successfully:', userData);
          // Handle both direct user data and wrapped responses
          const user = userData?.data || userData;
          setUser(user);
        })
        .catch((error) => {
          console.error('Failed to fetch user data:', error);
          // Only remove tokens if it's a 401 error (invalid/expired token)
          if (error.response?.status === 401 || error.message?.includes('Session expired')) {
            localStorage.removeItem('access_token');
            localStorage.removeItem('refresh_token');
          }
        })
        .finally(() => {
          setLoading(false);
        });
    } else {
      console.log('No access token found');
      setLoading(false);
    }
  }, []);

  const login = async (email, password) => {
    try {
      const response = await authService.login(email, password);

      // Handle standardized response format
      if (response && response.success === true && response.data) {
        const { access_token, refresh_token, user } = response.data;

        if (access_token && refresh_token) {
          localStorage.setItem('access_token', access_token);
          localStorage.setItem('refresh_token', refresh_token);
          setUser(user);
          return { success: true };
        } else {
          throw new Error('Missing tokens in login response');
        }
      } else {
        throw new Error(response?.error || 'Invalid response format from login endpoint');
      }
    } catch (error) {
      console.error('Login error:', error);
      return { 
        success: false, 
        error: error.response?.data?.detail || error.response?.data?.error || error.message || 'Login failed' 
      };
    }
  };

  const register = async (userData) => {
    try {
      const response = await authService.register(userData);

      // Handle standardized response format
      if (response && response.success === true && response.data) {
        return { success: true, user: response.data };
      } else {
        throw new Error(response?.error || 'Invalid response format from registration endpoint');
      }
    } catch (error) {
      console.error('Registration error:', error);
      return { 
        success: false, 
        error: error.response?.data?.detail || error.response?.data?.error || error.message || 'Registration failed' 
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    setUser(null);
  };

  const updateUser = (userData) => {
    setUser(userData);
  };

  const value = {
    user,
    loading,
    login,
    register,
    logout,
    updateUser
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}
