import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { User, Mail, Phone, Camera, Mic, Play, Square, CheckCircle } from 'lucide-react';
import { publicInterviewService } from '../services/publicInterviewService';
import { aiService } from '../services/aiService';
import { avatarService } from '../services/avatarService';
import toast from 'react-hot-toast';

const PublicInterview = () => {
  const { interviewId } = useParams();
  const navigate = useNavigate();
  
  const [step, setStep] = useState('loading'); // loading, register, interview, complete
  const [interview, setInterview] = useState(null);
  const [candidate, setCandidate] = useState(null);
  const [session, setSession] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [responses, setResponses] = useState([]);
  
  // Form data
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: ''
  });
  
  // Media refs
  const videoRef = React.useRef(null);
  const avatarRef = React.useRef(null);
  const mediaRecorderRef = React.useRef(null);
  const streamRef = React.useRef(null);
  const recordingIntervalRef = React.useRef(null);

  useEffect(() => {
    loadInterview();
    return () => cleanup();
  }, [interviewId]);

  const loadInterview = async () => {
    try {
      const interviewData = await publicInterviewService.getPublicInterview(interviewId);
      setInterview(interviewData);
      setStep('register');
    } catch (error) {
      console.error('Failed to load interview:', error);
      toast.error('Interview not found or no longer available');
      setStep('error');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const candidateData = await publicInterviewService.registerCandidate(interviewId, formData);
      setCandidate(candidateData);
      
      // Start interview session
      const sessionData = await publicInterviewService.startSession(interviewId, candidateData.id);
      setSession(sessionData);
      
      setStep('interview');
      initializeInterview();
    } catch (error) {
      console.error('Registration failed:', error);
      toast.error('Failed to register for interview');
    }
  };

  const initializeInterview = async () => {
    try {
      if (avatarRef.current) {
        avatarService.initialize(avatarRef.current);
      }
      await initializeMediaDevices();
      startQuestion();
    } catch (error) {
      console.error('Failed to initialize interview:', error);
      toast.error('Failed to initialize interview session');
    }
  };

  const initializeMediaDevices = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
      
      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (error) {
      console.error('Failed to access media devices:', error);
      toast.error('Camera and microphone access required');
    }
  };

  const startQuestion = async () => {
    if (!interview || !interview.questions || currentQuestion >= interview.questions.length) {
      completeInterview();
      return;
    }

    const question = interview.questions[currentQuestion];
    
    try {
      avatarService.setExpression('serious');
      const audioBlob = await aiService.generateSpeech(question.text);
      await avatarService.startSpeaking(audioBlob, question.text);
      
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);
      audio.play();
      setIsPlaying(true);
      
      audio.onended = () => {
        setIsPlaying(false);
        avatarService.stopSpeaking();
        avatarService.setExpression('neutral');
      };
      
    } catch (error) {
      console.error('Failed to start question:', error);
      toast.error('Failed to generate question audio');
    }
  };

  const startRecording = () => {
    if (!streamRef.current) {
      toast.error('Media stream not available');
      return;
    }

    try {
      const mediaRecorder = new MediaRecorder(streamRef.current, {
        mimeType: 'video/webm;codecs=vp9,opus'
      });
      
      mediaRecorderRef.current = mediaRecorder;
      const chunks = [];
      
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };
      
      mediaRecorder.onstop = async () => {
        const blob = new Blob(chunks, { type: 'video/webm' });
        await submitAnswer(blob);
      };
      
      mediaRecorder.start();
      setIsRecording(true);
      setRecordingTime(0);
      
      recordingIntervalRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
      
    } catch (error) {
      console.error('Failed to start recording:', error);
      toast.error('Failed to start recording');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      
      if (recordingIntervalRef.current) {
        clearInterval(recordingIntervalRef.current);
        recordingIntervalRef.current = null;
      }
    }
  };

  const submitAnswer = async (mediaBlob) => {
    try {
      const responseData = {
        audio_file: mediaBlob,
        response_text: `Answer to question ${currentQuestion + 1}`
      };
      
      await publicInterviewService.submitResponse(session.id, currentQuestion + 1, responseData);
      
      toast.success('Answer submitted successfully!');
      setCurrentQuestion(prev => prev + 1);
      setRecordingTime(0);
      
      // Move to next question or complete
      if (currentQuestion + 1 >= interview.questions.length) {
        completeInterview();
      } else {
        setTimeout(() => startQuestion(), 2000);
      }
      
    } catch (error) {
      console.error('Failed to submit answer:', error);
      toast.error('Failed to submit answer');
    }
  };

  const completeInterview = async () => {
    try {
      await publicInterviewService.completeSession(session.id);
      setStep('complete');
      toast.success('Interview completed successfully!');
    } catch (error) {
      console.error('Failed to complete interview:', error);
      toast.error('Failed to complete interview');
    }
  };

  const cleanup = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
    }
    if (recordingIntervalRef.current) {
      clearInterval(recordingIntervalRef.current);
    }
    avatarService.destroy();
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (step === 'loading') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading interview...</p>
        </div>
      </div>
    );
  }

  if (step === 'error') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Interview Not Found</h1>
          <p className="text-gray-600 mb-6">This interview is no longer available.</p>
          <button
            onClick={() => navigate('/')}
            className="btn-primary"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  if (step === 'register') {
    return (
      <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md mx-auto">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-gray-900">Join Interview</h1>
            <p className="mt-2 text-gray-600">{interview?.job_role}</p>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <form onSubmit={handleRegister} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Full Name
                </label>
                <div className="relative">
                  <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <input
                    type="text"
                    required
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    placeholder="Enter your full name"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email Address
                </label>
                <div className="relative">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <input
                    type="email"
                    required
                    value={formData.email}
                    onChange={(e) => setFormData({...formData, email: e.target.value})}
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    placeholder="Enter your email"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Phone Number (Optional)
                </label>
                <div className="relative">
                  <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <input
                    type="tel"
                    value={formData.phone}
                    onChange={(e) => setFormData({...formData, phone: e.target.value})}
                    className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    placeholder="Enter your phone number"
                  />
                </div>
              </div>

              <button
                type="submit"
                className="w-full btn-primary"
              >
                Start Interview
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }

  if (step === 'interview') {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-2xl font-bold text-gray-900">{interview?.job_role} Interview</h1>
                <p className="text-gray-600">Question {currentQuestion + 1} of {interview?.questions?.length || 0}</p>
              </div>
              <div className="text-right">
                <p className="text-sm text-gray-500">Candidate: {candidate?.name}</p>
                <p className="text-sm text-gray-500">Session: {session?.session_id?.slice(0, 8)}...</p>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Left Column - Avatar and Question */}
            <div className="space-y-6">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">AI Interviewer</h2>
                <div 
                  ref={avatarRef}
                  className="w-full h-64 bg-gradient-to-br from-blue-100 to-purple-100 rounded-lg flex items-center justify-center"
                />
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Current Question</h2>
                <div className="bg-gray-50 rounded-lg p-4">
                  <p className="text-gray-800 text-lg">
                    {interview?.questions?.[currentQuestion]?.text || 'No question available'}
                  </p>
                </div>
                
                <div className="mt-4 flex items-center justify-center">
                  <button
                    onClick={startQuestion}
                    disabled={isPlaying}
                    className="btn-primary flex items-center space-x-2"
                  >
                    <Play className="h-4 w-4" />
                    <span>{isPlaying ? 'Playing...' : 'Ask Question'}</span>
                  </button>
                </div>
              </div>
            </div>

            {/* Right Column - Video and Recording */}
            <div className="space-y-6">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Your Video</h2>
                <div className="relative">
                  <video
                    ref={videoRef}
                    autoPlay
                    muted
                    playsInline
                    className="w-full h-64 bg-gray-900 rounded-lg object-cover"
                  />
                  
                  {isRecording && (
                    <div className="absolute top-4 right-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm font-medium">
                      REC {formatTime(recordingTime)}
                    </div>
                  )}
                </div>
                
                <div className="mt-4 flex items-center justify-center space-x-4">
                  {!isRecording ? (
                    <button
                      onClick={startRecording}
                      className="btn-primary flex items-center space-x-2"
                    >
                      <Mic className="h-4 w-4" />
                      <span>Start Recording</span>
                    </button>
                  ) : (
                    <button
                      onClick={stopRecording}
                      className="btn-danger flex items-center space-x-2"
                    >
                      <Square className="h-4 w-4" />
                      <span>Stop Recording</span>
                    </button>
                  )}
                </div>
              </div>

              <div className="bg-white rounded-lg shadow-sm p-6">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Progress</h2>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-primary-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${((currentQuestion + 1) / (interview?.questions?.length || 1)) * 100}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-600 mt-2">
                  {currentQuestion + 1} of {interview?.questions?.length || 0} questions completed
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (step === 'complete') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <CheckCircle className="mx-auto h-16 w-16 text-green-500 mb-4" />
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Interview Completed!</h1>
          <p className="text-gray-600 mb-6">Thank you for completing the interview. Your responses have been recorded.</p>
          <div className="space-y-4">
            <p className="text-sm text-gray-500">
              Interview: {interview?.job_role}
            </p>
            <p className="text-sm text-gray-500">
              Candidate: {candidate?.name}
            </p>
            <p className="text-sm text-gray-500">
              Session ID: {session?.session_id}
            </p>
          </div>
        </div>
      </div>
    );
  }

  return null;
};

export default PublicInterview;
